import Header from '../components/Header';

let howMuch;

function JPY() {
	return (
		<div>
			<Header />
			<div className="text-center">
				<span>請輸入您想要換匯的金額</span><input id="howMuch_input" onChange={change} />日圓
				
			</div>
		</div>
	)
}

function change() {
	howMuch = document.getElementById('howMuch_input').attribute('value');
	console.log(howMuch);
}

export default JPY
