import Header from '../components/Header';

let howMuch;
let rate;
let currencyA;
let currencyB;



var xmlHTTP;

function $_xmlHttpRequest() {   
    if (window.ActiveXObject) {
        xmlHTTP = new ActiveXObject("Microsoft.XMLHTTP");
    } else if(window.XMLHttpRequest) {
        xmlHTTP = new XMLHttpRequest();
    }
}

function check() {  
    $_xmlHttpRequest();
    xmlHTTP.open("GET","http://data.fixer.io/api/latest?access_key=e8984e205db89c1c5523644643ed7d28&symbols=USD,AUD,CAD,PLN,MXN",true);
    xmlHTTP.onreadystatechange = function check_user() {
        if (xmlHTTP.readyState == 4 && xmlHTTP.status == 200) {
            var str = xmlHTTP.responseText;
            document.getElementById("message").innerHTML = str;
        }
    }
}
xmlHTTP.send(null);



function JPY() {
	return (
		<div>
			<div id="message" className="d-none"></div>
			<Header />
			<div className="text-center">
				我想要將
				<select id="currencyA" onChange={renewCurrency}>
					<option value="NTD">新台幣</option>
					<option value="JPY">日圓</option>
					<option value="USD">美金</option>
				</select>
				換成
				<select id="currencyB" onChange={renewCurrency}>
					<option value="NTD">新台幣</option>
					<option value="JPY">日圓</option>
					<option value="USD">美金</option>
				</select>

				<br />

				請輸入您想要換匯的金額<input id="howMuch_input" onChange={change} />新台幣<br />
				可換<input id="new_input" />日圓
			</div>
		</div>
	)
}

function renewCurrency() {
	currencyA = document.getElementById('currencyA').value;
	currencyB = document.getElementById('currencyB').value;
	console.log(currencyA + currencyB);
}

function change() {
	howMuch = document.getElementById('howMuch_input').value;
	let new_howMuch = howMuch*3.4423;
	console.log(new_howMuch);
	document.getElementById('new_input').setAttribute('value', new_howMuch);
}

export default JPY
