import Link from 'next/link';

const linkStyle = {
    marginRight: 15
}



var xmlHTTP;

function $_xmlHttpRequest() {   
    if (window.ActiveXObject) {
        xmlHTTP = new ActiveXObject("Microsoft.XMLHTTP");
    } else if(window.XMLHttpRequest) {
        xmlHTTP = new XMLHttpRequest();
    }
}

function check() {  
    $_xmlHttpRequest();
    xmlHTTP.open("GET","http://data.fixer.io/api/latest?access_key=e8984e205db89c1c5523644643ed7d28",true);
    xmlHTTP.onreadystatechange = function check_user() {
        if (xmlHTTP.readyState == 4 && xmlHTTP.status == 200) {
            var str = xmlHTTP.responseText;
            all_rate = JSON.parse(str);
            console.log(all_rate);
        }
    }
    xmlHTTP.send(null);
}

check();




const Header = () => (
    <div>
        <script src="https://code.jquery.com/jquery-3.3.1.slim.min.js" integrity="sha384-q8i/X+965DzO0rT7abK41JStQIAqVgRVzpbzo5smXKp4YfRvH+8abtTE1Pi6jizo" crossorigin="anonymous"></script>
        <script src="https://cdnjs.cloudflare.com/ajax/libs/popper.js/1.14.3/umd/popper.min.js" integrity="sha384-ZMP7rVo3mIykV+2+9J3UJ46jBk0WLaUAdn689aCwoqbBJiSnjAK/l8WvCWPIPm49" crossorigin="anonymous"></script>
        <link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.1.1/css/bootstrap.min.css" integrity="sha384-WskhaSGFgHYWDcbwN70/dfYBj47jz9qbsMId/iRN3ewGhXQFZCSftd1LZCfmhktB" crossorigin="anonymous" />
        <script src="https://stackpath.bootstrapcdn.com/bootstrap/4.1.1/js/bootstrap.min.js" integrity="sha384-smHYKdLADwkXOn1EmN1qk/HfnUcbVRZyYmZ4qpPea6sjB/pTJ0euyQp0Mk8ck+5T" crossorigin="anonymous"></script>
        <meta http-equiv="Content-Type" content="text/html; charset=utf-8" />

        <div className="container p-3">
            <div className="row">
                <div className="col-sm-4 text-center">
                    <Link href="/">
                        <a style={linkStyle}>首頁</a>
                    </Link>
                </div>
                <div className="col-sm-4 text-center">
                    <Link href="/change_currency">
                        <a style={linkStyle}>換匯</a>
                    </Link>
                </div>
                <div className="col-sm-4 text-center">
                    <Link href="/check_currency">
                        <a style={linkStyle}>查匯率</a>
                    </Link>
                </div>
            </div>
        </div>
    </div>
)

export default Header;
