import Header from '../components/Header';

let howMuch;

function JPY() {
	return (
		<div>
			<Header />
			<div className="text-center">
				請輸入您想要換匯的金額<input id="howMuch_input" onChange={change} />新台幣<br />
				可換<input id="new_input" />日圓
			</div>
		</div>
	)
}

function change() {
	howMuch = document.getElementById('howMuch_input').value;
	let new_howMuch = howMuch*3.4423;
	document.getElementById('howMuch_input').value = new_howMuch;
}

export default JPY
