import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

import Header from '../components/Header';


class BarGraph extends React.Component {
	constructor(props) {
		super(props);

		this.state({month_rate: null});
	}

	componentDidMount() {

		let month_rate = [];

		let base = 'EUR';
		let currency = 'JPY';

		let today = new Date();

		let self = this;

		for(let i=0; i<30; i++) {
			let date = new Date();
			date.setDate(today.getDate()-i);


			axios.get('http://data.fixer.io/api/'+date+'?access_key=e8984e205db89c1c5523644643ed7d28&base='+base+'&symbols='+currency)
			.then(function (response) {
				//self.setState({all_rate: response});
				month_rate.push(response.rates[currency]);
				this.setState({month_rate: month_rate});
			})
			.catch(function (error) {
			})
		}
	}

	render() {

		let today = new date();
		let date = today.getFullYear()+'-'+today.getMonth()+'-'+today.getDate();

		return (
			<div>
			<Header />
			<BarGraph month_rate={this.state.month_rate} />
			</div>
		)
	}
}





export default check_currency
