import Header from '../components/Header';
import {check} from '../components/Get_rate';
import {all_rate} from '../components/Get_rate';



check();

let howMuch;
let selected_rate;
let currencyA = 'EUR';
let currencyB = 'EUR';



class SelectCurrency extends React.Component {
	render() {

		let temp_arr = [<option value="nothing">請選擇</option>];

		for (let item in all_rate.rates) {
			let temp_str;

			temp_str = <option value={item}>{item}</option>;
			temp_arr.push(temp_str);
		}

		return <select>{temp_arr}</select>;
	}
}


function change_currency() {
	return <SelectCurrency />;
}

