import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

import Header from '../components/Header';
//import BarGraph from '../components/BarGraph';


class check_currency extends React.Component {
	constructor(props) {
		super(props);
		this.state = {month_rate: []};
	}

	componentDidMount() {

		let month_rate = [];

		let base = 'EUR';
		let currency = 'JPY';

		let today = new Date();

		let self = this;

		for(let i=0; i<30; i++) {
			let date = new Date();
			date.setDate(today.getDate()-i);
			date = date = today.getFullYear()+'-'+today.getMonth()+'-'+today.getDate();
			console.log(date);


			axios.get('http://data.fixer.io/api/'+date+'?access_key=e8984e205db89c1c5523644643ed7d28&base='+base+'&symbols='+currency)
			.then(function (response) {
				console.log(response);
				//self.setState({all_rate: response});
				month_rate.push(response.rates[currency]);
				this.setState({month_rate: month_rate});
			})
			.catch(function (error) {
			})
			//console.log(month_rate);
		}
		console.log(month_rate);
		setTimeout(
			function() {
				console.log(month_rate);
			}, 1000
		)
	}

	render() {
		//let today = new Date();
		//let date = today.getFullYear()+'-'+today.getMonth()+'-'+today.getDate();
		//console.log(this.state.month_rate);

		return (
			<div>
				<Header />
				<BarGraph month_rate={this.state.month_rate} />
			</div>
		)
	}
}



class BarGraph extends React.Component {
	render() {
		let list = ['bo'];

		for (let i=0; i<30; i++) {
			list.push(<SingleBar today_rate={this.props.month_rate[i]} />)
			//console.log(this.props.month_rate[i]);
		}

		return (
			<div>
				{list}yo
			</div>
		)
	}
}


class SingleBar extends React.Component {
	render() {
		const height = this.props.today_rate;

		const style = {
			backgroundColor: 'red',
			height: height
		}
		//console.log(this.props.today_rate);

		return (
			<div style={style}>
				{this.props.today_rate}
			</div>
		)
	}
}



export default check_currency
