import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';

import Header from '../components/Header';
//import BarGraph from '../components/BarGraph';


class check_currency extends React.Component {
	constructor(props) {
		super(props);
		this.componentDidMount = this.componentDidMount.bind(this);
		this.state({month_rate: null});
	}

	componentDidMount() {

		let month_rate = [];

		let base = 'EUR';
		let currency = 'JPY';

		let today = new Date();

		let self = this;

		for(let i=0; i<30; i++) {
			let date = new Date();
			date.setDate(today.getDate()-i);


			axios.get('http://data.fixer.io/api/'+date+'?access_key=e8984e205db89c1c5523644643ed7d28&base='+base+'&symbols='+currency)
			.then(function (response) {
				//self.setState({all_rate: response});
				month_rate.push(response.rates[currency]);
				this.setState({month_rate: month_rate});
			})
			.catch(function (error) {
			})
		}
	}

	render() {

		let today = new date();
		let date = today.getFullYear()+'-'+today.getMonth()+'-'+today.getDate();

		return (
			<div>
				<Header />
				<BarGraph month_rate={this.state.month_rate} />
			</div>
		)
	}
}


import { SingleBar } from './SingleBar';

class BarGraph extends ReactDOM.Component {

	render() {
		let list = [];

		for (let i=0; i<30; i++) {
			list.push(<SingleBar today_rate={this.props.month_rate.i} />)
		}

		return (
			<div>
				{list}
			</div>
		)
	}
}


export default check_currency
