import React from 'react'
import { connect } from 'react-redux'


class ShopBanner extends React.Component {
	render() {
		let shop = this.props.shop;
		let star = <img src="/static/star-on.svg" style={{height: '14px'}}/>

		return (
			<div>
				<li>
					<div style={{marginTop: '21px', marginBottom: '21px', marginLeft: '14px', backgroundColor: '#ffffff'}}>
						<div style={{display: 'inline-block', width: '97px'}}>
							<img src={shop.logo} style={{width: '97px'}} />
						</div>
						<div style={{display: 'inline-block'}}>
							<p style={{fontSize: '25px', color: '#4a4a4a'}}>{shop.name}</p>
							<p style={{fontSize: '16px', color: '#8d8c8c'}}>{shop.addr}</p>
							<div style={{position: 'relative'}}>
									{star}{star}{star}{star}{star}
								<div style={{position: 'absolute', right: '0', top: '0'}}>
									<img src="/static/pin.svg" style={{height: '12.7px'}}/>
									<span style={{fontSize: '14px', color: '#bf3829'}}>{shop.distance}km</span>
								</div>
							</div>
						</div>
					</div>
				</li>
			</div>
		)
	}
}

const mapStateToProps = state => ({
	shop: state.data[state.data_now.index_shop]
})

export default connect(
	mapStateToProps,
	null
)(ShopBanner)
