import React from 'react'
import ShopItem from './ShopItem'



class ShopList extends React.Component {
	render() {
		let shops = this.props.shops;
		let selectShop = this.props.selectShop;
		let nextPage = this.props.nextPage;
		let toggleMode = this.props.toggleMode;
		
		return (
			<div>
				
				
				<div className="container-fluid" style={{padding: '0', paddingTop: '70px'}}>
					
					<div className="d-none d-md-block" style={{position: 'relative', width: '100%', height: '66px', zIndex: '1'}} onClick={() => toggleMode()}>
						<p style={{lineHeight: '66px', fontSize: '26px', letterSpacing: '-0.63px', color: '#232323'}}>優惠列表</p>
						<img src="/static/right-on.svg" style={{position: 'absolute', right: '24px', top: '13px', width: '90px'}} />
					</div>
					
					
					<div className="row" style={{margin: '0'}}>	
						
						<ul className="col-12 col-md-4 list-unstyled" style={{padding: '0 6px 10px 6px'}}>
							{shops.map((shop, index) => (
								<ShopItem key={index} onClick={() => {selectShop(index); nextPage();}} content={shop} />
							))}
						</ul>
						
					</div>
				</div>

				<div className="d-block d-md-none position-fixed" style={{right: '12px', bottom: '9px'}} onClick={() => toggleMode()}><img src="/static/left-on.svg" style={{width: '90px'}} /></div>
			</div>
		)
	}
}
//<span key={index} onClick={() => {selectShop(index); nextPage();}}>{shop.name}</span>

/*
const ShopList = ({ shops }) => (
  <div>
    {shops.map(shop => (
	    <div>
	      	<span>{shop.name}</span>
	    </div>
    ))}
  </div>
)
*/

export default ShopList
