import React from "react"
import { compose, withProps } from "recompose"
import { withScriptjs, withGoogleMap, GoogleMap, Marker } from "react-google-maps"

import { connect } from 'react-redux'

const MyMapComponent = compose(
	withProps({
		googleMapURL: "https://maps.googleapis.com/maps/api/js?key=AIzaSyCt95qTXK2VBIGWxB8CiCWe8eBV475edOg&v=3.exp&libraries=geometry,drawing,places",
		loadingElement: <div style={{ height: `100%` }} />,
		containerElement: <div style={{ height: `400px` }} />,
		mapElement: <div style={{ height: `100%` }} />,
	}),
	withScriptjs,
	withGoogleMap
)((props) =>
	<GoogleMap
		defaultZoom={15}
		defaultCenter={{ lat: 24.986913, lng: 121.455948 }}
	>
		{props.isMarkerShown && <Marker position={{ lat: 24.986913, lng: 121.455948 }} onClick={props.onMarkerClick} />}
		{props.isMarkerShown && <Marker position={{ lat: 24.987, lng: 121.456 }} onClick={props.onMarkerClick} />}
	</GoogleMap>
)

class ShopMap extends React.PureComponent {
	state = {
		isMarkerShown: true,
	}

	componentDidMount() {
		this.delayedShowMarker()
	}

	delayedShowMarker = () => {
		setTimeout(() => {
			this.setState({ isMarkerShown: true })
		}, 3000)
	}

	handleMarkerClick = () => {
		this.setState({ isMarkerShown: false })
		this.delayedShowMarker()
	}

	render() {
		return (
			<MyMapComponent
				isMarkerShown={this.state.isMarkerShown}
				onMarkerClick={this.handleMarkerClick}
				//
				shops = {this.props.shops}
				
			/>
		)
	}
}




const mapStateToProps = state => ({
	shops: state.data
})
/*
const mapDispatchToProps = dispatch => ({
	previousPage: () => dispatch(previousPage())
})
*/
export default connect(
	mapStateToProps,
	null
)(ShopMap)

//export default ShopMap
