import React from 'react'

class CouponItem extends React.Component {
	render() {



		return (
			<li>
				<div style={{backgroundColor: '#fff6ee', height: '44px', margin: '6px'}} onClick={this.props.onClick}>
					

					<div style={{display: 'flex'}}>
						<p style={{fontSize: '15px', color: '#4a4a4a', flex: '1'}}>{this.props.content.title}</p>
						<p className="d-none d-md-block">活動時間：{this.props.content.startDate} - {this.props.content.endDate}</p>
						<img src="/static/next-page_2.svg" style={{width: '8px', height: '15px'}} />
						
					</div>
					
					
					
				</div>
			</li>
		)
	}
}


export default CouponItem
