import React from 'react'

class CouponItem extends React.Component {
	render() {



		return (
			<li style={{backgroundColor: '#fff6ee', margin: '6px', boxShadow: '3px 3px 3px #999', borderLeft: '8px #ffffff solid'}} onClick={this.props.onClick}>		
				<div style={{display: 'flex'}}>
					<div style={{display: 'inline-block', verticalAlign: 'middle', flex: '1'}}>
						<p style={{fontSize: '15px', color: '#4a4a4a', marginTop: '12px', marginBottom: '0', marginLeft: '13px'}}>{this.props.content.title}</p>
						<p style={{fontSize: '14px', color: '#9b9b9b'}} className="d-none d-md-block">活動時間：{this.props.content.startDate} - {this.props.content.endDate}</p>
					</div>
					<img src="/static/next-page_2.svg" style={{display: 'inline-block', width: '8px', height: '15px', verticalAlign: 'middle'}} />
				</div>				
			</li>
		)
	}
}


export default CouponItem
