import React from 'react'

class CouponItem extends React.Component {
	render() {
		return (
			<li style={{backgroundColor: '#fff6ee', borderRadius: '2px', boxShadow: '0 2px 4px 0 rgba(163, 162, 162, 0.5)', borderLeft: '8px #ffffff solid'}} onClick={this.props.onClick}>		
				<div style={{display: 'flex', position: 'relative'}}>
					<div style={{flex: '1'}}>
						<p style={{fontSize: '15px', color: '#4a4a4a', letterSpacing: '-0.36px', marginTop: '12px', marginBottom: '0', marginLeft: '13px'}}>{this.props.content.title}</p>
						<p style={{fontSize: '14px', color: '#9b9b9b', letterSpacing: '-0.34px'}} className="d-none d-md-block">活動時間：{this.props.content.startDate} - {this.props.content.endDate}</p>
					</div>
					<img src="/static/next-page_2.svg" style={{width: '8px', height: '15px', position: 'absolute', top:'50%', right: '0', transform: 'translate(0, -50%)'}} />
				</div>				
			</li>
		)
	}
}


export default CouponItem
