import React from 'react'
import Link from 'next/link'
import { connect } from 'react-redux'

import { previousPage } from '../actions'


class Header extends React.Component {
	render() {
		let previousPage = this.props.previousPage;
		let title = () => {
			switch (this.props.page) {
				case 'shop_list':
					console.log('yo');
					return '好康多'
				case 'coupon_list':
					console.log('yo');
					return '優惠項目'
				case 'coupon_content':
					console.log('yo');
					return '優惠內容'
				default:
					console.log('no');
					return null
			}
		}

		return (

				<div className="position-fixed" style={{height: '48px', width: '100%', backgroundColor: '#bf3829', zIndex: '1'}}>
					<span className="position-absolute" style={{top: '15px', left: '0'}} onClick={() => previousPage()}><img src="/static/slidemenu.svg" className="position-absolute" style={{height: '18px'}} /></span>
					<span className="position-absolute" style={{top: '5px', left: '24px'}}><img src="/static/app_logo.png" style={{height: '61px', width: '58px'}} /></span>
					<span className="position-absolute" style={{left: '91px', fontSize: '17px', color: '#FFF'}}>{title()}</span>
					<span className="position-absolute" style={{top: '15px', right: '31px'}} onClick={() => {previousPage(); previousPage();}}><img src="/static/home.svg" className="position-absolute" style={{height: '19px'}} /></span>
				</div>

		)
	}
}

const mapStateToProps = state => ({
	page: state.page_now
})

const mapDispatchToProps = dispatch => ({
	previousPage: () => dispatch(previousPage())
})


export default connect(
	mapStateToProps,
	mapDispatchToProps
)(Header)
