import React from 'react'

import ShopBanner from './ShopBanner'

class CouponContent extends React.Component {
	render() {

		let content = this.props.content


		return (
			<div>
				<ShopBanner />
				<div style={{backgroundColor: '#ffffff', padding: '13px', margin: '6px', boxShadow: '3px 3px 3px #999'}}>
					<p style={{fontSize: '17px'}}>{content.title}</p>
					<p style={{fontSize: '14px', color: '#9b9b9b'}}>活動時間：{this.props.content.startDate} - {this.props.content.endDate}</p>
					<p style={{fontSize: '14px', color: '#4a4a4a'}}>{content.detail}</p>
				</div>
				<div style={{margin: '6px'}}>
					<p style={{fontSize: '13px', color: '#d0021b'}}>注意事項</p>
					<p style={{fontSize: '13px', color: '#4a4a4a'}}>{content.note}</p>
				</div>
			</div>
		)
	}
}

export default CouponContent



