import React from 'react'

class CouponItem extends React.Component {
	render() {
		return (
			<li>
				<div style={{backgroundColor: '#fff6ee'}} onClick={this.props.onClick}>
					<div></div>
					<p style={{fontSize: '15px', color: '#4a4a4a'}}>{this.props.content.title}</p>
					<p className="d-none d-md-block">活動時間：{this.props.content.startDate} - {this.props.content.endDate}</p>
				</div>
			</li>
		)
	}
}


export default CouponItem
