import React from 'react'

class CouponContent extends React.Component {
	render() {

		let content = this.props.content


		return (
			<div>
				<h3>{content.title}</h3>
				<p>活動時間：{this.props.content.startDate} - {this.props.content.endDate}</p>
				<p>{content.detail}</p>
				<p>{content.note}</p>
				<p>{content.eventPic}</p>
				{
					content.eventPic.map((pic) => (
						<img src={pic.url} />
					))
				}
				
			</div>
		)
	}
}

export default CouponContent
