import React from 'react'

class CouponItem extends React.Component {
	render() {



		return (
			<li>
				<div style={{backgroundColor: '#fff6ee', height: '44px'}} onClick={this.props.onClick}>
					<div></div>
					<p style={{fontSize: '15px', color: '#4a4a4a'}}>{this.props.content.title}</p>
					<p className="d-none d-md-block">活動時間：{this.props.content.startDate} - {this.props.content.endDate}</p>
					<span className="position-absolute" style={{right: '18px'}}><img src="/static/next-page.svg" style={{width: '8px'}}/></span>
				</div>
			</li>
		)
	}
}


export default CouponItem
