import React from 'react'
import Link from 'next/link'
import { connect } from 'react-redux'

import { previousPage } from '../actions'

import { Button } from 'react-bootstrap';


class Header extends React.Component {
	render() {

		let previousPage = this.props.previousPage;

		let button_style = {

		}


		return (
			<div>
				<link rel="stylesheet" href="https://stackpath.bootstrapcdn.com/bootstrap/4.1.1/css/bootstrap.min.css" integrity="sha384-WskhaSGFgHYWDcbwN70/dfYBj47jz9qbsMId/iRN3ewGhXQFZCSftd1LZCfmhktB" crossorigin="anonymous" />
				<script src="https://stackpath.bootstrapcdn.com/bootstrap/4.1.1/js/bootstrap.min.js" integrity="sha384-smHYKdLADwkXOn1EmN1qk/HfnUcbVRZyYmZ4qpPea6sjB/pTJ0euyQp0Mk8ck+5T" crossorigin="anonymous"></script>
				<meta http-equiv="Content-Type" content="text/html; charset=utf-8" />

				<div className="position-fixed" style={{height: '48px',width: '100%', backgroundColor: '#bf3829'}}>
					<div style={{height: '48px',width: '100%'}}></div>
					<button className="btn position-absolute" onClick={() => previousPage()}><img src="/static/slidemenu.svg" /></button>
					<button className="btn btn-warning" onClick={() => {previousPage(); previousPage();}}>home</button>
				</div>
			</div>
		)
	}
}
/*
const mapStateToProps = state => ({
	page: state.page_now
})
*/
const mapDispatchToProps = dispatch => ({
	previousPage: () => dispatch(previousPage())
})




export default connect(
	null,
	mapDispatchToProps
)(Header)


